Change 2: Replace:
  Options options;
  options.wal_buffer_size = 4 * 1024 * 1024; // 4MB
with:
  Options options;
  options.wal_buffer_size = 16 * 1024 * 1024; // 16MB
Change 3: Replace:
  Status DBImpl::Write(const WriteOptions& options, WriteBatch* batch) {
      // ...
      if (options.sync) {
          // Synchronous WAL write
          // ...
      }
      // ...
  }
with:
  Status DBImpl::Write(const WriteOptions& options, WriteBatch* batch) {
      // ...
      if (options.sync) {
          // Group commit for multiple writes
          group_commit_->AddBatch(batch);
          if (group_commit_->ShouldFlush()) {
              group_commit_->Flush();
          }
      }
      // ...
  }

